void cf_run_target(bool dumpResult)
{
    classify(rsaP, sizeof(rsaP));
    classify(rsaQ, sizeof(rsaQ));
    classify(rsaD, sizeof(rsaD));

    mbedtls_rsa_import_raw(&rsa, rsaN, sizeof(rsaN), rsaP, sizeof(rsaP), rsaQ, sizeof(rsaQ), rsaD, sizeof(rsaD), rsaE, sizeof(rsaE));
    mbedtls_rsa_complete(&rsa);

    unsigned char *sig = malloc(bits / 8);
    mbedtls_rsa_pkcs1_sign(&rsa, myrng, NULL, MBEDTLS_MD_NONE, sizeof(m), m, sig);
    
    if(dumpResult)
        dump_secret(sig, bits / 8, "signature");

    free(sig);
}

void cf_prepare_next(void)
{
    // Increment message
    for(int i = 0; i < sizeof(m); ++i)
    {
        unsigned char tmp = m[i] + 1;
        m[i] = tmp;
        if(tmp != 0)
            break;
    }
}
